import React, { useEffect, useState } from "react";
import axios from "axios";

function Home(){
    const [ticketCount, setTicketCount] = useState(null);

    const [user, setUser] = useState(null);

    useEffect(() => {
      const fetchUser = async () => {
        try {
          const res = await axios.get("http://localhost:4000/profile", { withCredentials: true });
          setUser(res.data);
        } catch (err) {
          setUser(null);
        }
      };
      fetchUser();
    }, []);

    useEffect(() => {
        const fetchTicketCount = async () => {
        try {
            const res = await axios.get("http://localhost:4000/api/tickets/count", {
            withCredentials: true 
            });
            setTicketCount(res.data);
        } catch (err) {
            console.error(err);
            setTicketCount(null);
        }
        };
        fetchTicketCount();
    }, []);

    useEffect(() => {
        const fetchLastDrawnNumbers = async () => {
            try {
                const res = await axios.get(
                    "http://localhost:4000/api/tickets/lastDrawnNumbers",
                    { withCredentials: true }
                );
                setLastDrawnNumbers(res.data.numbers);
            } catch (err) {
                console.error(err);
                setLastDrawnNumbers(null);
            }
        };
        fetchLastDrawnNumbers();
    }, []);

    if (!user) {
        return (
        <div>
            <h2>Loto 6/45</h2>
            <p>Prijavi se:</p>
            <a href="http://localhost:4000/login">
            <button>Prijava</button>
            </a>
        </div>
        );
    }

    return (
        <div>
            <h2>Loto 6/45</h2>
            <p>Prijavljen korisnik: {user.name}</p>
            <a href="http://localhost:4000/logout">
                <button>Logout</button>
            </a>

            <p>
                Broj uplaćenih listića u trenutnom kolu:{" "}
                {ticketCount !== null ? ticketCount : "prazno"}
            </p>

            {lastDrawnNumbers ? (
                <p>Izvučeni brojevi iz posljednjeg kola: {lastDrawnNumbers}</p>
            ) : (
                <p>Nema završenog kola</p>
            )}

            {user && (
                <a href="/TicketForm">
                <button>Uplata listića</button>
                </a>
        )}
        </div>
    );
}

export default Home;
