import React, { useState } from "react";
import axios from "axios";
import { auth } from 'express-openid-connect';

import { useAuth0 } from "@auth0/auth0-react";

function TicketForm() {
  const { loginWithRedirect, logout, user, isAuthenticated } = useAuth0();

  const [personalId, setPersonalId] = useState("");
  const [numbers, setNumbers] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess(null);

    if (!isAuthenticated) {
      setError("Morate biti prijavljeni da pošaljete listić");
      return;
    }

    try {
      const res = await axios.post(
        "http://localhost:4000/api/tickets",
        { personalId, numbers }
      );
      setSuccess(res.data);
    } catch (err) {
      if (err.response && err.response.data) {
        setError(err.response.data.error);
      } else {
        setError("Greška pri slanju podataka");
      }
    }
  };

  return (
    <div>
      <h2>Unos loto listića</h2>

      {!isAuthenticated ? (
        <button onClick={() => loginWithRedirect()}>Login</button>
      ) : (
        <button onClick={() => logout({ returnTo: window.location.origin })}>Logout</button>
      )}

      {isAuthenticated && <p>Prijavljeni ste kao: {user.email}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Broj osobne iskaznice / putovnice:</label><br />
          <input
            type="text"
            value={personalId}
            maxLength={20}
            onChange={(e) => setPersonalId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Brojevi (6-10, odvojeni zarezom):</label><br />
          <input
            type="text"
            value={numbers}
            onChange={(e) => setNumbers(e.target.value)}
            required
          />
        </div>
        <button type="submit">Pošalji listić</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && (
        <div style={{ color: "green" }}>
          <p>Listić uspješno poslan!</p>
          <p>Ticket ID: {success.ticketId}</p>
          <img src={success.qrUrl} alt="QR Code" />
        </div>
      )}

    </div>
  );
}

export default TicketForm;
