//       } catch (err) {
//         console.error(err);
//         setTicketCount(null);
//       }
//     };
//     fetchTicketCount();
//   }, []);

    if (!user) {
        return (
        <div>
            <h2>Molimo prijavite se</h2>
            <a href="http://localhost:4000/login">
            <button>Login</button>
            </a>
        </div>
        );
    }

    return (
        <div>
            <p>Prijavljen korisnik: {user.name}</p>
            <a href="http://localhost:4000/logout">
                <button>Logout</button>
            </a>

            <p>
                Broj uplaćenih listića u trenutnom kolu:{" "}
                {/* {ticketCount !== null ? ticketCount : "prazno"} */}
            </p>

            {user && (
                <a href="/TicketForm">
                <button>Uplata listića</button>
                </a>
        )}
        </div>
    );
}

export default Home;
